generated.css:
    *{box-sizing:border-box;}
    *:before, *:after{box-sizing: border-box;}
    *{margin:0; padding:0;}
    ul,ol{ list-style:none; }
    a{
        color:inherit;
        text-decoration: none;
    }

    img{max-width:100%;
    max-height:100%;}

    body{background: #eee;}
        /* 虽然body的范围很小但是添加背景色后，会只能的平铺整个网页 */
    .globalHeader{ 
    margin:20px;
    
    }
    .searchForm{
        display:flex;
        justify-content: space-between;
    }
    .searchForm > input{ 
       width:100%;
        margin-right:20px;
        height:40px;
        border:1px solid #ddd;
        padding: 0 10px; 
       border-radius:4px;
    }
    .searchForm > button {
       white-space:nowrap;
        padding:0 28px;
        border:1px solid #ddd;
        border-radius:4px;
        background: #0282B9;
        color:white;
        font-size:16px;
        }

    .globalMain{
    
    }

    .siteList{
      display:flex;
       flex-wrap:wrap;

       justify-content:space-between;
    }
    .siteList > li{
      margin:0 20px;
       margin-bottom:10px;

    }
    .siteList .site {

        width:160px;
        display:flex;
        justify-content: space-between;
        flex-direction:column;
        align-items:center;
    background:white;
    border:1px solid #ddd;
    border-radius:4px;
    padding: 20px 0;
}
    
.siteList .site > .logo{
    width:64px;
    height:64px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 64px;
}
.siteList.site > .link {
    font-size: 14px;
    margin-top: 4px;
}


.siteList .addButton{
    width:160px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction:column;
    padding:20px 0;
    background:white;
    border-radius:4px;
    border:1px solid #ddd; 

}

.siteList .addButton .icon{
    width:94px;
    height:94px;
}

.siteList .addButton .text{
    font-size: 14px;
    margin-top: 4px;
}

.siteList .addButton .iconWrapper{ 
    
    width:64px;
    height:64px;
    display: flex;
    justify-content: center;
    align-items: center;
}
